Scripts.OnMouseDown:
if(arg1	== "LeftButton")	then
	this:StartMoving();
	this.isMoving	=	true;
end

Scripts.OnMouseUp:
if(this.isMoving) then
	this:StopMovingOrSizing();
	this.isMoving	=	false;
end

Scripts.OnShow:
local sideWidth = getglobal(this:GetName().."Left"):GetWidth() *2;
local textWidth = getglobal(this:GetName().."Text"):GetWidth();
getglobal(this:GetName().."Middle"):SetWidth(textWidth);
getglobal(this:GetName().."MiddleDisabled"):SetWidth(textWidth);
this:SetWidth(sideWidth + textWidth);

Scripts.OnEnter:
if(this:GetID()	== 0) then
	MetaMapNotes_OnEnter(0);
elseif(this:GetID()	== 1) then
	MetaMapNotes_OnEnter(-1);
end

Scripts.OnMouseDown:
if ( arg1	== "LeftButton"	)	then
	this:GetParent():StartMoving();
	this.isMoving	=	true;
end

Scripts.OnMouseUp:
if ( this.isMoving ) then
	this:GetParent():StopMovingOrSizing();
	this.isMoving	=	false;
	WorldMapFrame:SetUserPlaced(false);
end

Scripts.OnEnter:
MetaMap_ButtonTooltip();
if(not MetaMapOptions.MenuMode)	then
	MetaMapMenu_OnShow("Minimap");
end

Scripts.OnMouseUp:
if(arg1	== "RightButton" and MetaMapOptions.MenuMode)	then
	MetaMapMenu_OnShow("Minimap");
end

Scripts.OnMouseDown:
if(arg1	== "LeftButton") then
	if(IsShiftKeyDown() or IsControlKeyDown()) then
		MiniMapCoords_OnClick();
	else
		this:StartMoving();
	end
end
	this.isMoving	=	true;

Scripts.OnMouseUp:
if(this.isMoving) then
	this:StopMovingOrSizing();
	this.isMoving	=	false;
end

Scripts.OnEnter:
WorldMapTooltip:SetOwner(this, "ANCHOR_CENTER");
WorldMapTooltip:SetText("Tooltip Scaling Sample",	0, 1,	0);
WorldMapTooltip:AddLine("Move	the	Tooltip	slider to	change the scale of	the	tooltips shown on	the	WorldMap", 1,	1, 1,	true);
WorldMapTooltip:Show();

Scripts.OnLoad:
if(IsAddOnLoaded("Titan")) then
	TitanPanelMetaMapButton_OnLoad();
	TitanPanelButton_OnLoad();
else
	this:Hide();
end

Scripts.OnMouseUp:
if(arg1	== "RightButton" and MetaMapOptions.MenuMode)	then
	MetaMapMenu_OnShow("Titan");
end

Scripts.OnKeyUp:
if(arg1	== GetBindingKey("METAMAP_FSTOGGLE") or	arg1 ==	GetBindingKey("METAMAP_SAVESET"))	then
	MetaMap_FullScreenToggle();
end

Scripts.OnMouseDown:
if(arg1	== "LeftButton")	then
	this:StartMoving();
end
	this.isMoving	=	true;

Scripts.OnMouseUp:
if(this.isMoving) then
	this:StopMovingOrSizing();
	this.isMoving	=	false;
end
