Scripts.OnTextChanged:
if ( ( this:GetText() == nil ) or ( this:GetText() == "" ) ) then
	NuNTransmit:Disable();
else
	NuNTransmit:Enable();
end

Scripts.OnEscapePressed:
if ( NuNSearchFrame.backEnabled ) then
	NuNSearch_Back();
else
	HideUIPanel(NuNSearchFrame);
end

